package outros;
 
public class OperadoresDeAtribuicaocompostos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num10 = 10;
		int num11 = 10;
		int num12 = 10;
		double num13 = 10D;
		
		num10 += 3;  // É quivalente a:  numero recebe ele mesmo + 3
		System.out.println(num10);
		
		num11 -= 3; // É quivalente a:  numero recebe ele mesmo - 3
		System.out.println(num11);
		
		num12 *= 3; // É quivalente a:  numero recebe ele mesmo * 3
		System.out.println(num12);

		num13 /= 3 ;// É quivalente a:  numero recebe ele mesmo / 3
		System.out.println(num13);
	}

}
